import { Loading } from 'element-ui';
import printPaper from "./printPaper";
import vueEasyPrint from "vue-easy-print";

export default {
  inject: ['reload'],
  data() {
    return {
      tabledata:[],
      tableData: [],
      currentNum: 1,
      pageSize: 10,
      total: 0,
      tableDataShow: false,
      subjectList: [],
      classList: [],
      dataShow: true,
      formShow: false,
      examShow: false,
      form: {
        date2: '',
        date1: '',
        num: 15,
        classId: '',
        subjectId: '',
        titleType: '',
        titleName: '',
        examDate: '',
        paperNum: ''
      },
      dataList: [],
    };
  },
  components: { vueEasyPrint,printPaper },

  methods: {
    PrintRow(index, row){
      this.$print(this.$refs.print) // 使用
    },

    updateTitle(dataList){
      let jsonArr = [];
      let newArr = dataList.filter((item, i) => {
        console.log("循环==i==", item);
        let listArr = item.list.filter((list,i)=>{
          let obj = {};
          obj.id = list.id;
          obj.answer = list.answer;
          jsonArr.push(JSON.stringify(obj))
        })
      })
      let params = {
        titleString:jsonArr,
      };
      this.axios
          .post('/zj/title/updateTitleByList.htm', params)
          .then(
              function (response) {
                if (response.data.code == '0000') {
                  this.$message.success('提交成功');
                  this.reload();
                } else {
                  this.$message.error('提交失败');
                }
              }.bind(this))
          .catch(function (error) {
            console.log(error);
          });
    },

    checkForm() {
      let all = [];
      this.dataList.map((x) => {``
        x.list &&
        x.list.map((y) => {
          all.push({ id: y.id, val: y.val });
        });
      });
      this.$emit('updateTitle', all);
    },

    endLoading() {    //使用Element loading-close 方法
      let loadingInstance = Loading.service();
      this.$nextTick(() => { // 以服务的方式调用的 Loading 需要异步关闭
        loadingInstance.close();
      });
    },

    onSubmit() {
      let params = {
        paperName: this.form.titleName,
        subjectId: this.form.subjectId,
        difficulty: this.form.titleType,
        classId: this.form.classId,
        titleStatus: this.form.titleStatus,
        titleAnswer: this.form.titleAnswer,
        paperDate: this.form.date1,
        examDate: this.form.examDate,
        paperNum: this.form.paperNum
      };
      Loading.service();
      this.axios
          .post('/zj/title/audioExam.htm', params)
          .then(
              function (response) {
                if (response.data.code == '0000') {
                  this.$message.success('组卷成功,请点击返回试卷然后查看');
                  this.formShow = false;
                  this.examShow = true;
                  this.endLoading();
                } else {
                  this.$message.error(response.data.description);
                  this.endLoading();
                }
              }.bind(this)
          )
          .catch(function (error) {
            this.endLoading();
            console.log(error);
          });
    },

    collBackPaperPage() {
      this.dataShow = true;
      this.formShow = false;
      this.examShow = false;
      this.queryPaperPage();
      this.reload();
    },

    changePage(page) {
      //设置页码
      this.currentNum = page;
      this.queryPaperPage();
    },

    changePageSize(pageSize) {
      //设置每页展示数量
      this.pageSize = pageSize;
      this.queryPaperPage();
    },

    handleChange(value) {
      console.log(value);
    },

    paperCancel(row) {
      this.dataShow = true;
      this.formShow = false;
      this.examShow = false;
    },
    handleEdit() {
      this.dataShow = false;
      this.formShow = true;
      this.examShow = false;
    },

    exportPaper(paperId){
      let param = {
        paperId:paperId
      };
      const form = document.createElement('form');
      form.id = 'form';
      form.name = 'form';
      document.body.appendChild(form);
      for (const obj in param) {
        if (param.hasOwnProperty(obj)) {
          const input = document.createElement('input');
          input.tpye = 'hidden';
          input.name = obj;
          input.value = param[obj];
          form.appendChild(input);
        }
      }
      form.method = 'GET'; //请求方式
      form.action = '/zj/paper/paperExport.htm';
      form.submit();
      document.body.removeChild(form);
    },

    handleDelete(row) {
      let params = {
        paperId: row.paperId
      };
      this.axios
          .post('/zj/paper/deletePaper.htm', params)
          .then(
              function (response) {
                if (response.data.code == "0000") {
                  this.$message.success("删除成功");
                  this.reload();
                } else {
                  this.$message.error("删除失败");
                }
              }.bind(this)
          )
          .catch(function (error) {
            console.log(error);
          });
    },

    queryPaperPage() {
      let params = {
        currentNum: this.currentNum,
        pageSize: this.pageSize
      };
      this.axios
          .post('/zj/title/queryPaperPage.htm', params)
          .then(
              function (response) {
                this.tableData = response.data.result.list;
                // this.total = response.data.result.count;
                this.tableDataShow = true;
              }.bind(this)
          )
          .catch(function (error) {
            console.log(error);
          });
    },

    queryPaper(row) {
      let params = {
        paperId: row.paperId
      };
      this.axios
          .post('/zj/title/queryPaper.htm', params)
          .then(
              function (response) {
                if (response.data.code == '0000') {
                  this.formShow = false;
                  this.dataShow = false;
                  this.examShow = true;
                  this.dataList.push({
                    list: this.changeArray(response.data.result.oneList1, 1, 'type'),

                  });
                  this.dataList.push({
                    list: this.changeArray(response.data.result.oneList2, 2, 'type'),
                  });
                  this.dataList.push({
                    list: this.changeArray(response.data.result.oneList3, 2, 'type'),
                  });
                  console.log(this.dataList);
                } else {
                  this.$message({message: '该试卷错误', type: 'error'});
                }
              }.bind(this)
          )
          .catch(function (error) {
            console.log(error);
          });
    },
    changeArray(one, val, name) {
      one.map((x) => {
        x[name] = val;
        x['val'] = '';
      });
      return one;
    },
    querySubjectList() {
      this.axios
          .post('/zj/sub/queryListBySub.htm')
          .then(
              function (response) {
                this.subjectList = response.data.result;
              }.bind(this)
          )
          .catch(function (error) {
            console.log(error);
          });
    },
    queryClassList() {
      this.axios
          .post('/zj/class/queryList.htm')
          .then(
              function (response) {
                this.classList = response.data.result;
              }.bind(this)
          )
          .catch(function (error) {
            console.log(error);
          });
    },

  },
  created() {
    this.querySubjectList();
    this.queryClassList();
    this.queryPaperPage();
  }
};
